mapper.update:
UPDATE queue_user_info
access_no= #{queueUserInfo.accessNo,jdbcType=VARCHAR}, hospital_code= #{queueUserInfo.hospitalCode,jdbcType=VARCHAR}, create_date= #{queueUserInfo.createDate,jdbcType=TIMESTAMP}, update_date= #{queueUserInfo.updateDate,jdbcType=TIMESTAMP}, open_id= #{queueUserInfo.openId,jdbcType=VARCHAR}
WHERE id = #{queueUserInfo.id,jdbcType=VARCHAR}

mapper.select:
SELECT OPEN_ID
FROM QUEUE_USER_INFO
WHERE  ACCESS_NO = #{accessNo,jdbcType=VARCHAR} AND HOSPITAL_CODE = #{hospitalCode,jdbcType=VARCHAR}

